import React from "react";
import { ProcessNumberBadge } from "./ProcessNumberBadge";
import { ProcessCard } from "./ProcessCard";

const processSteps = [
  {
    number: "01",
    title: "ご相談",
    description: "お客様のビジョンを<br />お聞かせください。<br />まずはXのリプライかDMでお気軽にご連絡ください。"
  },
  {
    number: "02",
    title: "ヒアリング",
    description: "詳細な要望をお伺いするための<br />ヒアリングを行います。<br />ご希望の内容や予算、納期などをお知らせください。"
  },
  {
    number: "03",
    title: "デザイン制作",
    description: "ヒアリング内容をもとに、<br />オリジナルデザインを制作します。<br />馬の特徴や厩舎のイメージを大切にしたデザインをご提案します。"
  },
  {
    number: "04",
    title: "レビュー・修正",
    description: "制作したデザインをご確認いただき、必要に応じて修正を行います。<br />ご満足いただけるまで調整いたします。"
  },
  {
    number: "05",
    title: "印刷・制作",
    description: "最終デザインが確定したら、<br />印刷・制作に進みます。<br />高品質な仕上がりを実現するため、信頼できる業者と連携しています。"
  },
  {
    number: "06",
    title: "お届け",
    description: "完成した商品をお届けします。<br />印刷所から直接お届けする場合もございます。"
  }
];

export default function Process() {
  return (
    <section className="overflow-hidden whitespace-nowrap">
      <div className="flex relative flex-col items-center px-20 py-16 w-full min-h-[762px] max-md:px-5 max-md:max-w-full">
        <img
          src="https://cdn.builder.io/api/v1/image/assets/d22efd85dd1e4c99a198cddf2a2a638b/bcbd7e22bef8b422aebcfd3bc499b206ca37673a?placeholderIfAbsent=true"
          className="object-cover absolute inset-0 size-full"
          alt=""
        />
        <header className="relative text-center">
          <h1 className="text-5xl font-bold leading-none text-white max-md:text-4xl">
            制作プロセス
          </h1>
          <p className="mt-5 text-2xl font-semibold leading-none text-white max-md:max-w-full">
            安心してご依頼いただけるよう、明確なプロセスでお進めします
          </p>
        </header>
        <div className="flex relative flex-wrap gap-7 items-start self-start mt-14 w-full max-w-[1013px] max-md:mt-10 max-md:max-w-full">
          {processSteps.map((step, index) => (
            <div
              key={index}
              className="flex grow shrink items-start min-w-60 w-[262px]"
            >
              <ProcessNumberBadge number={step.number} />
              <ProcessCard
                title={step.title}
                description={step.description}
              />
            </div>
          ))}
        </div>
      </div>
    </section>
  );
}
