import React from "react";

export const PortfolioCard = ({
  image,
  title,
  description,
  altText = ""
}) => {
  return (
    <div className="relative shrink-0 h-[602px] w-[994px] max-md:h-[450px] max-md:w-[700px] max-sm:h-[350px] max-sm:w-[300px]">
      <img
        src={image}
        alt={altText}
        className="object-cover absolute top-0 left-0 rounded-lg h-full w-full"
      />
      <div
        className="absolute top-0 left-0 w-full h-full rounded-lg pointer-events-none"
        style={{
          height: '100%',
          width: '100%',
          background: 'linear-gradient(to top, rgba(0,0,0,0.7) 20%, rgba(0,0,0,0) 40%)'
        }}
      />
      <h3 className="absolute w-auto h-9 text-3xl font-bold leading-9 text-white left-[50px] top-[474px] max-md:text-2xl max-md:leading-8 max-md:top-[350px] max-sm:inset-x-5 max-sm:w-auto max-sm:text-lg max-sm:leading-6 max-sm:top-[270px]">
        {title}
      </h3>
      <p className="absolute w-auto h-7 text-lg leading-7 text-gray-200 left-[50px] max-w-[647px] top-[526px] max-md:text-base max-md:leading-6 max-md:top-[390px] max-sm:inset-x-5 max-sm:w-auto max-sm:text-sm max-sm:leading-5 max-sm:top-[300px]">
        {description}
      </p>
    </div>
  );
};
