import React, { useState, useEffect, useRef } from "react";
import { SceneCard } from "./SceneCard";
import { SliderArrow } from "./SliderArrow";

export function SceneSlider({ scenes }) {
  const [currentIndex, setCurrentIndex] = useState(0);
  const sliderRef = useRef(null);

  // --- スライドの枚数を画面幅に応じて決定 ---
  const getVisibleCount = () => {
    if (window.innerWidth >= 1280) return 4; // xl
    if (window.innerWidth >= 1024) return 3; // lg
    if (window.innerWidth >= 768) return 2; // md
    return 1; // sm and below
  };

  const [visibleCount, setVisibleCount] = useState(getVisibleCount());

  useEffect(() => {
    const handleResize = () => {
      setVisibleCount(getVisibleCount());
    };
    window.addEventListener('resize', handleResize);
    // 初期表示時にも実行
    handleResize(); 
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  // --- スライド操作 ---
  const totalSlides = Math.ceil(scenes.length / visibleCount);

  const handlePrev = () => {
    setCurrentIndex((prevIndex) => (prevIndex > 0 ? prevIndex - 1 : totalSlides - 1));
  };
  const handleNext = () => {
    setCurrentIndex((prevIndex) => (prevIndex < totalSlides - 1 ? prevIndex + 1 : 0));
  };

  const canPrev = true; // Loop so always possible
  const canNext = true;

  return (
    <div className="relative w-full max-w-7xl mx-auto px-4">
      <div className="overflow-hidden" ref={sliderRef}>
        <div
          className="flex transition-transform duration-500 ease-in-out"
          style={{ transform: `translateX(-${currentIndex * 100}%)` }}
        >
          {scenes.map((scene, index) => (
            <div key={scene.id} className="flex-shrink-0 w-full md:w-1/2 lg:w-1/3 xl:w-1/4 p-2">
              <SceneCard
                {...scene}
              />
            </div>
          ))}
        </div>
      </div>

      {/* 左右ボタン */}
      {canPrev && (
        <SliderArrow direction="left" onClick={handlePrev} variant="scene" />
      )}
      {canNext && (
        <SliderArrow direction="right" onClick={handleNext} variant="scene" />
      )}
    </div>
  );
}
