import React, { useState } from 'react';

export const AccordionHeader = ({
  title,
  isExpanded = false,
  onToggle
}) => {
  const [expanded, setExpanded] = useState(isExpanded);

  const handleToggle = () => {
    const newExpanded = !expanded;
    setExpanded(newExpanded);
    onToggle?.(newExpanded);
  };

  return (
    <header className="relative mx-auto my-0 h-auto w-full max-md:w-full max-md:max-w-[700px] max-sm:w-full max-sm:h-auto">
      <div className="absolute top-0 left-0 bg-black rounded-xl shadow-sm h-full w-full" />
      <h1 className="absolute text-2xl font-bold leading-8 text-white h-auto left-[18px] top-[23px] w-[90%] max-md:text-xl max-md:left-[15px] max-sm:left-3 max-sm:top-3.5 max-sm:text-lg max-sm:leading-8">
        {title}
      </h1>
      <div className="relative z-10 p-6 text-base text-white">
        2023年地方馬主資格取得、デザイナー<br />
        ダビスタで競馬に触れナリタブライアンや黄金世代に青春を費やしブランクがあったのちウマ娘で復帰。<br />
        馬券はからっきしなので主にG1馬応援。現在は共有馬のみ。<br />
        夢は一頭持ち、重賞出走、ドバイワールドカップ。
      </div>
      <button
        onClick={handleToggle}
        className="absolute text-3xl font-bold leading-8 text-center text-white h-[33px] right-[7px] top-[23px] w-[30px] max-md:text-2xl max-md:right-[15px] max-sm:right-3 max-sm:top-3.5 max-sm:text-2xl hover:opacity-80 transition-opacity"
        aria-label={expanded ? "Collapse section" : "Expand section"}
        aria-expanded={expanded}
      >
        {expanded ? "－" : "＋"}
      </button>
    </header>
  );
};
