import React from "react";
import { PortfolioCard } from "./PortfolioCard";
import { SliderArrow } from "./SliderArrow";

const portfolioItems = [
  {
    image: "/portfolio-horse-logo.jpg",
    title: "馬名ロゴ",
    description: "オリジナルグッズや出馬予告画像、メンコなどに利用"
  },
  {
    image: "/portfolio-luminaval-towel.jpg",
    title: "ルミナヴァル号フェイスタオル",
    description: "口取り式で掲げたり応援にも使えます"
  },
  {
    image: "/portfolio-age-runner-card.jpg",
    title: "エイジランナー号トレーディングカード",
    description: "サインを貰うのにも最適"
  },
  {
    image: "/portfolio-firmarpoint-card.jpg",
    title: "フェルマーポイント号優勝記念トレーディングカード",
    description: "話題になったゴリアット号のトレーディングカードと同じフォーマットで"
  },
  {
    image: "/portfolio-firmarpoint-stand.jpg",
    title: "フェルマーポイント号優勝記念アクリルスタンド",
    description: "口取り写真をアクリルスタンドに加工することで立体感のある特別な仕上がりに"
  },
  {
    image: "/portfolio-sun-or-slice-cap.jpg",
    title: "サンオルソーライス号キャップ",
    description: "関係者へのプレゼントにも、帽子タイプや刺繍なども選べます"
  },
  {
    image: "/portfolio-sun-or-slice-stand.jpg",
    title: "サンオルソーライス号重賞出走記念アクリルスタンド",
    description: "台座をゼッケンに、疾走中の写真を使うことで躍動感が有る仕上がりに"
  },
  {
    image: "/portfolio-south-express-shield.jpg",
    title: "サウスエクスプレス号蹄鉄盾",
    description: "蹄鉄は幸運のお守りともしられインテリアにも最適です"
  },
  {
    image: "/portfolio-pick-and-roll-shield.jpg",
    title: "ピックアンドロール号蹄鉄盾",
    description: "デザイン自由度の高い蹄鉄盾"
  },
  {
    image: "/portfolio-pick-and-roll-photo.jpg",
    title: "ピックアンドロール号勝利写真",
    description: "L版の勝利写真をカスタマイズ"
  }
];

const CARD_WIDTH = 900;
const CARD_HEIGHT = 600;
const GAP = 40;

export const PortfolioSection = () => {
  const [currentIndex, setCurrentIndex] = React.useState(0);
  const total = portfolioItems.length;

  const handlePrevious = () => {
    setCurrentIndex((prev) => (prev > 0 ? prev - 1 : 0));
  };

  const handleNext = () => {
    setCurrentIndex((prev) => (prev < total - 1 ? prev + 1 : total - 1));
  };

  return (
    <section id="portfolio" className="overflow-hidden relative w-full bg-neutral-900 h-[820px] max-sm:h-[600px] py-12">
      <div className="absolute top-0 left-0 bg-neutral-900 size-full" />

      <header className="text-center">
        <h1 className="absolute left-2/4 w-auto h-12 text-5xl font-bold leading-10 text-center text-white -translate-x-2/4 top-[66px] max-md:top-10 max-md:text-4xl max-md:leading-9 max-sm:text-3xl max-sm:leading-7 max-sm:top-[30px]">
          制作実績
        </h1>
        <h2 className="absolute left-2/4 w-auto h-8 text-2xl font-semibold leading-8 text-center text-white -translate-x-2/4 top-[134px] max-md:text-xl max-md:leading-7 max-md:top-[90px] max-sm:px-5 max-sm:py-0 max-sm:text-base max-sm:leading-6 max-sm:top-[70px]">
          これまでのデザイン実績の一部をご紹介します
        </h2>
      </header>

      <div className="w-full flex justify-center items-center overflow-hidden h-[600px] top-[212px] max-md:h-[400px] max-md:top-[150px] max-sm:h-[300px] max-sm:top-[120px] relative">
        <div
          className="flex gap-x-10 transition-transform duration-300 ease-in-out"
          style={{
            transform: `translateX(calc(50vw - 450px - ${currentIndex} * ${CARD_WIDTH + GAP}px))`
          }}
        >
          {portfolioItems.map((item, index) => (
            <div className="w-[900px] h-[600px] flex-shrink-0" key={index}>
              <PortfolioCard
                image={item.image}
                title={item.title}
                description={item.description}
                altText={`Portfolio item: ${item.title}`}
                cardWidth={CARD_WIDTH}
                cardHeight={CARD_HEIGHT}
              />
            </div>
          ))}
        </div>
        {/* インジケータードット */}
        <div className="absolute left-1/2 -translate-x-1/2 bottom-8 flex gap-2 z-10">
          {portfolioItems.map((_, idx) => (
            <span
              key={idx}
              className={`inline-block w-3 h-3 rounded-full ${currentIndex === idx ? 'bg-white' : 'bg-gray-500/50'} transition-colors`}
            />
          ))}
        </div>
        {/* 左右ボタン */}
        <SliderArrow direction="left" onClick={handlePrevious} />
        <SliderArrow direction="right" onClick={handleNext} />
      </div>
    </section>
  );
};

export default PortfolioSection;
