import React, { useEffect, useRef } from "react";
import { HeroContent } from "./HeroContent";

export function HeroHeader() {
  const videoRef = useRef(null);

  useEffect(() => {
    const video = videoRef.current;
    if (video) {
      video.playbackRate = 0.5; // スローモーション
      video.loop = true; // ループ再生を明示
    }
    // Parallax効果
    const handleScroll = () => {
      if (video) {
        const scrollY = window.scrollY;
        video.style.transform = `translateY(${scrollY * 0.2}px)`;
      }
    };
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <header className="relative overflow-hidden font-bold text-center whitespace-nowrap w-full h-[700px]">
      {/* 背景動画 */}
      <video
        ref={videoRef}
        className="absolute inset-0 w-full h-full object-cover z-0"
        src="/bg.mp4"
        autoPlay
        loop
        muted
        playsInline
        id="hero-bg-video"
        style={{ objectFit: 'cover' }}
      />
      {/* 黒半透明オーバーレイ */}
      <div className="absolute inset-0 bg-black bg-opacity-60 z-10" />
      {/* 前面コンテンツ */}
      <div className="relative z-20 w-full h-full flex items-center justify-center -translate-y-[50px]">
        <div className="flex flex-col items-center px-20 pt-52 pb-12 w-full max-md:px-5 max-md:pt-24 max-md:max-w-full">
          <HeroContent />
        </div>
      </div>
    </header>
  );
}

export default HeroHeader;
