import React from "react";
import { SceneSlider } from "./SceneSlider";

const sceneData = [
  {
    id: "retirement",
    imageSrc: "https://cdn.builder.io/api/v1/image/assets/d22efd85dd1e4c99a198cddf2a2a638b/5d3780d2f64c05358676283207e510bc06116e5e?placeholderIfAbsent=true",
    title: "引退記念",
    description: "愛馬の軌跡を形に残す記念グッズ全般",
    imageAspectRatio: "aspect-[1.18]",
    cardPadding: "pb-16"
  },
  {
    id: "victory",
    imageSrc: "https://cdn.builder.io/api/v1/image/assets/d22efd85dd1e4c99a198cddf2a2a638b/1d6f2f7cac63b4f9e1a04ce3846bfc5e2ed50163?placeholderIfAbsent=true",
    title: "勝利祝い",
    description: "栄光の瞬間を記念するグッズ全般",
    imageAspectRatio: "aspect-[1.59]",
    cardPadding: "pb-24"
  },
  {
    id: "daily",
    imageSrc: "https://cdn.builder.io/api/v1/image/assets/d22efd85dd1e4c99a198cddf2a2a638b/444a9497c55a1a668594d03645239a1be23acea0?placeholderIfAbsent=true",
    title: "日常使い",
    description: "スマホケースやモバイルバッテリーで愛馬を身近に",
    imageAspectRatio: "aspect-[1.59]",
    cardPadding: "pb-16"
  },
  {
    id: "fanservice",
    imageSrc: "https://cdn.builder.io/api/v1/image/assets/d22efd85dd1e4c99a198cddf2a2a638b/5c5ba0de3771d1a287d8574b913e9c3fa3451785?placeholderIfAbsent=true",
    title: "ファンサービス",
    description: "シールやバッジをファンへのプレゼントに",
    imageAspectRatio: "aspect-[1.18]",
    cardPadding: "pb-16"
  }
];

export default function UseCaseSection() {
  return (
    <section className="flex flex-col justify-center whitespace-nowrap py-12">
      <header className="text-center">
        <h1 className="text-4xl font-bold leading-none text-black max-md:max-w-full">
          活用シーン例
        </h1>
        <h2 className="mt-7 text-2xl font-semibold leading-none text-gray-900 max-md:max-w-full">
          様々なシーンでご活用いただけます
        </h2>
      </header>
      <SceneSlider scenes={sceneData} />
    </section>
  );
}
