import React from "react";
import { SceneCard } from "./SceneCard";

export function SceneSlider({ scenes }) {
  return (
    <div className="overflow-hidden mt-7 w-full max-md:max-w-full">
      <div className="flex gap-9 items-center max-md:max-w-full">
        {scenes.map((scene) => (
          <SceneCard
            key={scene.id}
            imageSrc={scene.imageSrc}
            title={scene.title}
            description={scene.description}
            imageAspectRatio={scene.imageAspectRatio}
            cardPadding={scene.cardPadding}
          />
        ))}
      </div>
    </div>
  );
}
