import React, { useState } from 'react';
import { MessageHeading } from './MessageHeading';
import { MessageSubtitle } from './MessageSubtitle';
import { MessageBody } from './MessageBody';
import { AccordionHeader } from './AccordionHeader';
import { CheckIcon } from './CheckIcon';

export const ProfileContent = () => {
  const [isAccordionOpen, setIsAccordionOpen] = useState(false);

  return (
    <div className="flex flex-col w-full">
      <MessageHeading>
        私も、あなたと同じ一人の馬主です。
      </MessageHeading>

      <MessageSubtitle>
        勝った馬も、勝てなかった馬も、みんな宝物。
      </MessageSubtitle>

      <MessageBody>
        はじめましてデザイナー馬主のUMAです。
        <br />
        このサービスを始めたのは、一般的なデザインでは表現しきれない、競馬の世界特有の
        <br />
        「熱量」や「物語」を形にしたかったから。
        <br />
        勝負服の色に込められた想いや、一戦一戦のドラマ。
        <br />
        その価値を、私はあなたの次くらい理解しています。
        <br />
        <br />
        あなたの愛馬との絆、厩舎の誇りを、唯一無二のデザインへ。
        <br />
        一つひとつの想いを丁寧に翻訳し、記憶に残る宝物をお届けすることをお約束します。
      </MessageBody>

      <div className="mt-8 md:mt-12">
        <AccordionHeader 
          title="プロフィール" 
          isOpen={isAccordionOpen} 
          setIsOpen={setIsAccordionOpen} 
        />
        <div 
          className={`overflow-hidden transition-all duration-500 ease-in-out ${isAccordionOpen ? 'max-h-screen opacity-100' : 'max-h-0 opacity-0'}`}
        >
          <div className="pt-4 pl-4 border-l-2 border-gray-200">
            <ul className="space-y-3 text-base text-gray-700">
              <li className="flex items-start"><CheckIcon className="mr-2 mt-1" /> JRA / 地方競馬 オーナー</li>
              <li className="flex items-start"><CheckIcon className="mr-2 mt-1" /> デザイン歴15年以上の現役デザイナー</li>
              <li className="flex items-start"><CheckIcon className="mr-2 mt-1" /> 愛馬との思い出を宝物にするお手伝い</li>
            </ul>
          </div>
        </div>
      </div>
    </div>
  );
};
