import React from "react";
import { PortfolioCard } from "./PortfolioCard";
import { SliderArrow } from "./SliderArrow";

const portfolioItems = [
  {
    image: "https://placehold.co/992x600/8B4513/8B4513",
    title: "馬名ロゴ",
    description: "オリジナルグッズや出馬予告画像、メンコなどに利用"
  },
  {
    image: "https://placehold.co/992x600/4169E1/4169E1",
    title: "ルミナヴァル号フェイスタオル",
    description: "口取り式で掲げたり応援にも使えます"
  },
  {
    image: "https://placehold.co/992x600/2E8B57/2E8B57",
    title: "エイジランナー号トレーディングカード",
    description: "サインを貰うのにも最適"
  },
  {
    image: "https://placehold.co/992x600/D2691E/D2691E",
    title: "フェルマーポイント号優勝記念トレーディングカード",
    description: "話題になったゴリアット号のトレーディングカードと同じフォーマットで"
  },
  {
    image: "https://placehold.co/992x600/CD853F/CD853F",
    title: "フェルマーポイント号優勝記念アクリルスタンド",
    description: "口取り写真をアクリルスタンドに加工することで立体感のある特別な仕上がりに"
  },
  {
    image: "https://placehold.co/992x600/8B0000/8B0000",
    title: "サンオルソーライス号キャップ",
    description: "関係者へのプレゼントにも、帽子タイプや刺繍なども選べます"
  },
  {
    image: "https://placehold.co/992x600/483D8B/483D8B",
    title: "サンオルソーライス号重賞出走記念アクリルスタンド",
    description: "台座をゼッケンに、疾走中の写真を使うことで躍動感が有る仕上がりに"
  },
  {
    image: "https://placehold.co/992x600/556B2F/556B2F",
    title: "サウスエクスプレス号蹄鉄盾",
    description: "蹄鉄は幸運のお守りともしられインテリアにも最適です"
  },
  {
    image: "https://placehold.co/992x600/B8860B/B8860B",
    title: "ピックアンドロール号蹄鉄盾",
    description: "デザイン自由度の高い蹄鉄盾"
  },
  {
    image: "https://placehold.co/992x600/2F4F4F/2F4F4F",
    title: "ピックアンドロール号勝利写真",
    description: "L版の勝利写真をカスタマイズ"
  }
];

export const PortfolioSection = () => {
  const [currentIndex, setCurrentIndex] = React.useState(0);

  const handlePrevious = () => {
    setCurrentIndex((prev) => (prev > 0 ? prev - 1 : portfolioItems.length - 1));
  };

  const handleNext = () => {
    setCurrentIndex((prev) => (prev < portfolioItems.length - 1 ? prev + 1 : 0));
  };

  return (
    <section className="overflow-hidden relative w-full bg-neutral-900 h-[894px] max-sm:h-[600px]">
      <div className="absolute top-0 left-0 bg-neutral-900 size-full" />

      <header className="text-center">
        <h1 className="absolute left-2/4 w-auto h-12 text-5xl font-bold leading-10 text-center text-white -translate-x-2/4 top-[66px] max-md:top-10 max-md:text-4xl max-md:leading-9 max-sm:text-3xl max-sm:leading-7 max-sm:top-[30px]">
          制作実績
        </h1>
        <h2 className="absolute left-2/4 w-auto h-8 text-2xl font-semibold leading-8 text-center text-white -translate-x-2/4 top-[134px] max-md:text-xl max-md:leading-7 max-md:top-[90px] max-sm:px-5 max-sm:py-0 max-sm:text-base max-sm:leading-6 max-sm:top-[70px]">
          これまでのデザイン実績の一部をご紹介します
        </h2>
      </header>

      <div className="overflow-hidden absolute left-0 w-full h-[602px] top-[212px] max-md:h-[500px] max-md:top-[150px] max-sm:h-[400px] max-sm:top-[120px]">
        <div
          className="flex absolute top-0 left-2/4 gap-20 items-center w-auto -translate-x-2/4 h-[602px] max-md:gap-12 max-sm:gap-8 transition-transform duration-300 ease-in-out"
          style={{
            transform: `translateX(calc(-50% - ${currentIndex * (994 + 80)}px))`,
          }}
        >
          {portfolioItems.map((item, index) => (
            <PortfolioCard
              key={index}
              image={item.image}
              title={item.title}
              description={item.description}
              altText={`Portfolio item: ${item.title}`}
            />
          ))}
        </div>

        <SliderArrow
          direction="left"
          onClick={handlePrevious}
        />

        <SliderArrow
          direction="right"
          onClick={handleNext}
        />
      </div>
    </section>
  );
};

export default PortfolioSection;
